aki=$PWD ; for i in $(find . -type f -iname '*.zip') ; do j=$(basename ${i%.*}); unzip $i -d ${i%.*} ; cd ${i%.*} ; xsltproc --output dublin_core.xml /repositorio/users/trmurakami/Import/biomed-dez-2013/bmctodublincore.xsl "$j.xml" ; xsltproc --output metadata_usp.xml /repositorio/users/trmurakami/Import/biomed-dez-2013/bmctousp.xsl "$j.xml" ;  ls -1 | egrep -v '.xml$'  | grep -v 'contents' > contents ; rm "$j.xml" ;  cd $aki ; done